mapper.select:
SELECT cv.contractId, cv.adStartDate, cv.adEndDate, cv.contractAmount, cv.paymentDate, cv.influencerName, cv.advertiserName, cv.contractStatus, cv.contractLink FROM ( SELECT contractId FROM contract_view
contractId > #{cursor}
ORDER BY contractId ASC LIMIT #{size} ) AS temp JOIN contract_view cv ON cv.contractId = temp.contractId
