mapper.select:
SELECT a.admin_login_id AS adminSignUpId, a.admin_name AS adminName, a.admin_email AS adminEmail, a.admin_phone AS adminPhone, a.admin_status AS adminStatus FROM ( SELECT admin_login_id FROM admin WHERE admin_status = 'PENDING'
AND admin_login_id > #{cursor}
ORDER BY admin_login_id ASC LIMIT #{size} ) AS temp JOIN admin a ON a.admin_login_id = temp.admin_login_id
