mapper.select:
SELECT arv.advertiserReviewId, arv.advertiser, arv.writer, arv.rating, arv.reviewDate, arv.visibility, arv.review, arv.contractId FROM ( SELECT advertiserReviewId FROM advertiser_reviews_view
advertiserReviewId > #{cursor}
ORDER BY advertiserReviewId ASC LIMIT #{size} ) AS temp JOIN advertiser_reviews_view arv ON arv.advertiserReviewId = temp.advertiserReviewId
