mapper.select:
SELECT advertiser_review_id AS advertiserReviewId, '' AS advertiser, '' AS writer, advertiser_review_score AS rating, advertiser_review_created_at AS reviewDate, visibility AS visibility, advertiser_review_comment AS review, contract_id AS contractId FROM advertiser_review
advertiser_review_id > #{cursor}
ORDER BY advertiser_review_id ASC LIMIT #{size}
