mapper.select:
SELECT contract.contract_id AS contractId, CONCAT(IFNULL(advertiser.company_name, '없음'), '(', IFNULL(advertiser_user.user_name, '없음'), ')') AS advertiserName, CONCAT(IFNULL(channel.channel_name, '없음'),'(', IFNULL(influencer_user.user_name, '없음'), ')') AS influencerName, contract.contract_start_date AS adStartDate, contract.contract_end_date AS adEndDate, contract.contract_amount AS adAmount FROM ( SELECT DISTINCT contract.contract_id FROM contract JOIN proposal ON contract.proposal_id = proposal.proposal_id JOIN campaign ON proposal.campaign_id = campaign.campaign_id JOIN advertiser ON campaign.advertiser_id = advertiser.advertiser_id JOIN user AS advertiser_user ON advertiser.user_id = advertiser_user.user_id JOIN influencer ON influencer.influencer_id = proposal.influencer_id JOIN user AS influencer_user ON influencer.user_id = influencer_user.user_id JOIN channel ON channel.influencer_id = influencer.influencer_id
contract.contract_id > #{cursor}
ORDER BY contract.contract_id ASC LIMIT #{size} ) AS temp JOIN contract ON contract.contract_id = temp.contract_id LEFT JOIN proposal ON contract.proposal_id = proposal.proposal_id LEFT JOIN campaign ON proposal.campaign_id = campaign.campaign_id LEFT JOIN advertiser ON campaign.advertiser_id = advertiser.advertiser_id LEFT JOIN user AS advertiser_user ON advertiser.user_id = advertiser_user.user_id LEFT JOIN influencer ON influencer.influencer_id = proposal.influencer_id LEFT JOIN user AS influencer_user ON influencer.user_id = influencer_user.user_id LEFT JOIN channel ON channel.influencer_id = influencer.influencer_id
